/*创建一个--cartoon数据库*/
set names utf8;
drop database if exists cartoon;
create database cartoon charset=utf8;
use cartoon;

/*创建一张用户表userinfo*/
create table userinfo(
  u_id int auto_increment primary key,
  u_names varchar(255) not null unique,        /*用户名*/
  u_gender varchar(50),        /*用户性别*/
  u_pwd varchar(255) not null,          /*用户密码*/
  u_phone varchar(255) not null unique, /*用户手机号*/
  u_pic varchar(255),          /*用户头像*/
  u_email varchar(255) not null unique, /*用户邮箱*/
  u_member int                  /*是否为会员*/  
)ENGINE = InnoDB charset = utf8;

/*创建一张管理表admin*/
create table admin(
  a_id int auto_increment primary key,  
  a_names varchar(255) not null unique, /*管理名*/
  a_pwd varchar(255) not null,    /*管理密码*/
  a_pic varchar(255) not null    /*管理员头像*/     
)ENGINE = InnoDB charset = utf8;


/*创建一张漫画分类信息表message*/
create table message(
  m_id int auto_increment primary key, 
  m_img varchar(255) not null,      /*图片*/
  m_title varchar(255) not null ,   /*标题*/
  m_kind varchar(255) not null,      /*种类*/
  m_remark varchar(255),            /*描述*/
  m_details varchar(255)            /*详情*/
)ENGINE = InnoDB charset = utf8;


/*创建一张漫画分类收藏信息表message*/
create table messagelike(
